<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1" /><title>cycle</title><script language="JavaScript1.1"><!--/*JavaScript Image slideshow:By  (www.wsabstract.com)Over 200+ free JavaScript here!*/var slideimages=new Array()var slidelinks=new Array()function slideshowimages(){for (i=0;i<slideshowimages.arguments.length;i++){slideimages[i]=new Image()slideimages[i].src=slideshowimages.arguments[i]}}function slideshowlinks(){for (i=0;i<slideshowlinks.arguments.length;i++)slidelinks[i]=slideshowlinks.arguments[i]}function gotoshow(){if (!window.winslide||winslide.closed)winslide=window.open(slidelinks[whichlink])elsewinslide.location=slidelinks[whichlink]winslide.focus()}//--></script></head><body><a href="javascript:gotoshow()"><img src="food1.jpg" name="slide" border=0 width=461 height=183></a><script><!--//configure the paths of the images, plus corresponding target linksslideshowimages("images/home_main_1.jpg","images/home_main_2.jpg","images/home_main_3.jpg")slideshowlinks("http://www.thegermanfriday.com","http://www.thegermanfriday.com/about.php","http://www.thegermanfriday.com/contact.php")//configure the speed of the slideshow, in milisecondsvar slideshowspeed=5000var whichlink=0var whichimage=0function slideit(){if (!document.images)returndocument.images.slide.src=slideimages[whichimage].srcwhichlink=whichimageif (whichimage<slideimages.length-1)whichimage++elsewhichimage=0setTimeout("slideit()",slideshowspeed)}slideit()//--></script></body></html>